package Labsession2207;

public class WHILEloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int N = 10;

	        int sum = 0;
	        int i = 1;

	        while (i <= N) {
	            sum += i;
	            i++;
	        }

	        System.out.println("Sum of first " + N + " natural numbers is: " + sum);
	    }
	}


